import { createClient } from 'fetch-api';

// 创建API客户端
const api = createClient({
  baseUrl: 'https://api.example.com/v1',
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
  },
  timeout: 5000, // 5秒超时
  retries: 2,    // 失败自动重试次数
});

// 定义API端点
const endpoints = {
  // 获取用户信息
  getUser: (id) => api.get(`/users/${id}`),

  // 创建新用户
  createUser: (userData) => api.post('/users', { body: userData }),

  // 更新用户信息
  updateUser: (id, userData) => api.patch(`/users/${id}`, { body: userData }),

  // 删除用户
  deleteUser: (id) => api.delete(`/users/${id}`),

  // 文件上传
  uploadAvatar: (id, file) => {
    const formData = new FormData();
    formData.append('avatar', file);
    return api.post(`/users/${id}/avatar`, {
      body: formData,
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
  },
};

// 使用示例
async function fetchUserData(userId) {
  try {
    const user = await endpoints.getUser(userId);
    console.log('获取到用户信息:', user);
    return user;
  } catch (error) {
    if (error.status === 404) {
      console.error('用户不存在');
    } else {
      console.error('获取用户信息失败:', error);
    }
    throw error;
  }
}
